mapper.select:



		AND resource_name like concat('%', #{resourceName}, '%')


		AND visible = #{visible}


		AND status = #{status}


order by parent_id, order_num

		select distinct m.resource_id, m.parent_id, m.resource_name, m.path, m.component, m.`query`, m.visible, m.status, ifnull(m.perms,'') as perms, m.is_frame, m.is_cache, m.resource_type, m.icon, m.order_num, m.create_time
		from sys_resource m
		left join sys_role_resource rm on m.resource_id = rm.resource_id
		left join sys_user_role ur on rm.role_id = ur.role_id
		left join sys_role ro on ur.role_id = ro.role_id
		where ur.user_id = #{params.userId}

            AND m.resource_name like concat('%', #{resourceName}, '%')


            AND m.visible = #{visible}


            AND m.status = #{status}

		order by m.parent_id, m.order_num

select m.resource_id from sys_resource m left join sys_role_resource rm on m.resource_id = rm.resource_id where rm.role_id = #{roleId}
and m.resource_id not in (select m.parent_id from sys_resource m inner join sys_role_resource rm on m.resource_id = rm.resource_id and rm.role_id = #{roleId})
order by m.parent_id, m.order_num

mapper.update:
update sys_resource
resource_name = #{resourceName}, parent_id = #{parentId}, order_num = #{orderNum}, path = #{path}, component = #{component}, `query` = #{query}, is_frame = #{isFrame}, is_cache = #{isCache}, resource_type = #{resourceType}, visible = #{visible}, status = #{status}, perms = #{perms}, icon = #{icon}, remark = #{remark}, update_by = #{updateBy}, update_time = sysdate()
where resource_id = #{resourceId}
