mapper.select:
select u.user_id, u.user_studentid,u.user_sex, u.user_password, u.user_name, u.user_type, u.user_organization,u.user_email, u.user_phone,u.user_description,u.user_picture1, u.user_picture2,u.user_picture3, u.status, u.del_flag, u.create_by, u.create_time, u.remark, d.dept_name, d.leader from system_user u left join system_dept d on u.dept_id = d.dept_id where u.del_flag = '0'
AND u.user_id = #{userId}
AND u.user_name like concat('%', #{userName}, '%')
AND u.status = #{status}
AND u.phonenumber like concat('%', #{phonenumber}, '%')
AND date_format(u.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
AND date_format(u.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.dept_id FROM system_dept t WHERE find_in_set(#{deptId}, ancestors) ))
${params.dataScope}

select distinct u.user_id, u.dept_id, u.user_name, u.nick_name, u.email, u.phonenumber, u.status, u.create_time from system_user u left join system_dept d on u.dept_id = d.dept_id left join system_user_role ur on u.user_id = ur.user_id left join system_role r on r.role_id = ur.role_id where u.del_flag = '0' and r.role_id = #{roleId}
AND u.user_name like concat('%', #{userName}, '%')
AND u.phonenumber like concat('%', #{phonenumber}, '%')
${params.dataScope}

mapper.update:
update system_user
dept_id = #{deptId}, user_name = #{userName}, nick_name = #{nickName}, email = #{email}, phonenumber = #{phonenumber}, sex = #{sex}, avatar = #{avatar}, password = #{password}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, update_by = #{updateBy}, remark = #{remark}, update_time = sysdate()
where user_id = #{userId}
