mapper.select:
select u.user_id, u.user_studentid,u.user_sex, u.user_name, u.user_type, u.user_organization,u.user_campus,u.user_email, u.user_phone,u.user_qq,u.user_wechat,u.user_score,u.user_description,u.user_picture1, u.user_picture2,u.user_picture3, u.status, u.create_by, u.create_time, u.remark, d.dept_name, d.leader from system_user u left join system_dept d on u.dept_id = d.dept_id where u.del_flag = '0'
AND u.user_id = #{userId}
AND u.user_name like concat('%', #{userName}, '%')
AND u.status = #{status}
AND u.user_phone like concat('%', #{userPhone}, '%')
AND date_format(u.create_time,'%Y%m%d') >= date_format(#{params.beginTime},'%Y%m%d')
AND date_format(u.create_time,'%Y%m%d') <= date_format(#{params.endTime},'%Y%m%d')
AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.dept_id FROM system_dept t WHERE find_in_set(#{deptId}, ancestors) ))
${params.dataScope}

select distinct u.user_id, u.user_studentid,u.user_sex, u.user_name, u.user_type, u.user_organization,u.user_campus,u.user_email, u.user_phone,u.user_qq,u.user_wechat,u.user_score,u.user_description,u.user_picture1, u.user_picture2,u.user_picture3, u.status, u.create_by, u.create_time, u.remark from system_user u left join system_dept d on u.dept_id = d.dept_id left join system_user_role ur on u.user_id = ur.user_id left join system_role r on r.role_id = ur.role_id where u.del_flag = '0' and r.role_id = #{roleId}
AND u.user_name like concat('%', #{userName}, '%')
AND u.user_phone like concat('%', #{userPhone}, '%')
${params.dataScope}

mapper.update:
update system_user
user_studentid=#{userStudentid}, user_sex=#{userSex}, user_password=#{userPassword}, dept_id=#{deptId}, user_name=#{userName}, user_organization=#{userOrganization}, user_campus=#{userCampus}, user_type=#{userType}, user_score=#{userScore}, user_email=#{userEmail}, user_phone=#{userPhone}, user_qq=#{userQq}, user_wechat=#{userWechat}, user_description=#{userDescription}, user_picture1=#{userPicture1}, user_picture2=#{userPicture2}, user_picture3=#{userPicture3}, status=#{status}, update_by = #{updateBy}, remark = #{remark}, update_time = sysdate()
where user_id = #{userId}
